import React, {useState} from "react";
import ComboCard from "./ComboCard";
import Row from 'react-bootstrap/Row';
import { Container } from 'react-bootstrap';
import Accordian from 'react-bootstrap';

function Combinations() {
  const [cards, setCards] = useState([{
    title: "New Combo",
    content:"...",
  }]);

  function addCard(newCard) {
    setCards(prevCards => {
      return [...prevCards, newCard];
    });
  }

  function deleteCard(id) {
    setCards(prevCards => {
      return prevCards.filter((cardItem, index) => {
        return index !== id;
      });
    });
  }

  return <div>
    <h1 className="titles">Combinations</h1>
    
    <Accordion defaultActiveKey="0">
      <Accordion.Item eventKey="0">
        <Accordion.Header>Filters</Accordion.Header>
        <Accordion.Body>
          Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do
          eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad
          minim veniam, quis nostrud exercitation ullamco laboris nisi ut
          aliquip ex ea commodo consequat. Duis aute irure dolor in
          reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla
          pariatur. Excepteur sint occaecat cupidatat non proident, sunt in
          culpa qui officia deserunt mollit anim id est laborum.
        </Accordion.Body>
      </Accordion.Item>
    </Accordion>

    <Container>
    <Row xs={1} md={4} className="g-4">
    {cards.map((cardItem, index) => {
      return (
        <ComboCard
          key={index}
          id={index}
          title={cardItem.title}
          content={cardItem.content}
          buttonId={cardItem.buttonId}
          newCard={addCard}
          onDelete={deleteCard}
          setNum={cardItem.setNum}
          notes={cardItem.notes}
          counts={cardItem.counts}
          category={cardItem.category}
        />
      );
    })}
    </Row>
    </Container>
    <Container className="centeredContainer">
    </Container>


  </div>
}

export default Combinations;
