import React, {useState} from "react";
import ComboCard from "./ComboCard";
import Row from 'react-bootstrap/Row';
import { Container } from 'react-bootstrap';
import Accordion from 'react-bootstrap/Accordion';

function Combinations() {
  const [cards, setCards] = useState([{
    title: "New Combo",
    content:"...",
  }]);

  function addCard(newCard) {
    setCards(prevCards => {
      return [...prevCards, newCard];
    });
  }

  function deleteCard(id) {
    setCards(prevCards => {
      return prevCards.filter((cardItem, index) => {
        return index !== id;
      });
    });
  }

  return <div>
    <h1 className="titles">Combinations</h1>
    
    <Accordion defaultActiveKey="0">
      <Accordion.Item eventKey="0">
        <Accordion.Header>Filters</Accordion.Header>
        <Accordion.Body>
        <Form.Group className="mb-3" controlId="formBasicCheckbox">
        <Form.Check inline type="checkbox" label="Beginner" onClick={checkboxClick}/>
        <Form.Check inline type="checkbox" label="Intermediate" onClick={checkboxClick} />
        <Form.Check inline type="checkbox" label="Advanced" onClick={checkboxClick} />
        <Form.Check inline type="checkbox" label="Brain Teaser" onClick={checkboxClick} />
        <Form.Check inline type="checkbox" label="Warm Up"  onClick={checkboxClick}/>
        <Form.Check inline type="checkbox" label="From First" />
        <Form.Check inline type="checkbox" label="From Fifth" />
        <Form.Check inline type="checkbox" label="Tendue" />
        <Form.Check inline type="checkbox" label="Degage" />
        <Form.Check inline type="checkbox" label="Ronde Jambe" />
        <Form.Check inline type="checkbox" label="Adagio" />
        <Form.Check inline type="checkbox" label="Frappe" />
        <Form.Check inline type="checkbox" label="Grande Battement" />
        <Form.Check inline type="checkbox" label="Pas de Cheval" />
        <Form.Check inline type="checkbox" label="Pike" />
      </Form.Group>
        </Accordion.Body>
      </Accordion.Item>
    </Accordion>

    <Container>
    <Row xs={1} md={4} className="g-4">
    {cards.map((cardItem, index) => {
      return (
        <ComboCard
          key={index}
          id={index}
          title={cardItem.title}
          content={cardItem.content}
          buttonId={cardItem.buttonId}
          newCard={addCard}
          onDelete={deleteCard}
          setNum={cardItem.setNum}
          notes={cardItem.notes}
          counts={cardItem.counts}
          category={cardItem.category}
        />
      );
    })}
    </Row>
    </Container>
    <Container className="centeredContainer">
    </Container>


  </div>
}

export default Combinations;
