import React from "react";
import Box from '@mui/material/Box';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';
import Button from 'react-bootstrap/Button';

function ViewButtons() {

    function playCombo(){
        const elem = document.getElementById('front');
        elem.background.color = "success";

    }

    return (
      <Container>
         <Row className="row" >
      <Col></Col>
        <Col className="textHeading">
         <Button variant="light" id="front" onCall={playCombo}>Front</Button>
        </Col>
        <Col></Col> 
      </Row>

      <Row>
      <Col className="textHeading">
        <Button variant="light" id="inside" onCall={playCombo}>Inside</Button>
      </Col>
      <Col className="textHeading">
        <Row> <Button variant="light" id="first" onCall={playCombo}>First</Button></Row>
      </Col>
      <Col className="textHeading">
        <Button variant="light" id="side" onCall={playCombo}>Side</Button>
      </Col>
      </Row>
     
      <Row>
        <Col> </Col>
        <Col className="textHeading">
          <Button variant="light" id="back" onCall={playCombo}>Back</Button>
        </Col>
        <Col></Col>
      </Row>
      <Row></Row>
      <Row>
      <Button variant="primary" onClick={playCombo}>Play</Button>
      </Row>
      </Container>
    );
  }

export default ViewButtons;
