import React from 'react'
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import CreateModal from "./CreateModal"
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import ViewModal from "./ViewModal";
import '../custom.scss'


function ComboCard(props) {
  const cardId = props.id;
  const [modalShow, setModalShow] = React.useState(false);

  function handleClick() {
    props.onDelete(cardId);
  }

  return (
    <Col>
    <Card border="primary">
      <Card.Img variant="top" src="holder.js/300x200" />
      <Card.Body>
      {cardId === 0 ? "" :
        <Button variant="primary" onClick={handleClick}>
          Delete
      </Button>
      }
        <Card.Title><h2>{props.title}</h2></Card.Title>
        <Card.Text>
        <Row>
          <Col><p>Combination:</p></Col>
          <Col><p>{props.content}</p></Col>
        </Row>
          <Col><p>Sets of 8:</p></Col>
          <Col><p>{props.setNum}</p></Col>
        </Card.Text>
    <>
      <Button variant="primary" onClick={() => setModalShow(true)}> {cardId === 0 ? "New Combo" : "View Combo"}
      </Button>
      {cardId === 0 ? 
      <CreateModal
        show={modalShow}
        onHide={() => setModalShow(false)}
        submitCard = {props.newCard}
      />
      :
      <ViewModal
        title={props.title}
        content={props.content}
        sets={props.setNum}
        show={modalShow}
        onHide={() => setModalShow(false)}
      />
      }
    </>
       
      </Card.Body>
    </Card>
    </Col>
  );
}

export default ComboCard
